    SELECT 
    	DISTINCT(d.id),url,d.name,complication,symptoms,rate,time,d.status,d.created
    FROM 

    	tb_disease d 


    	(tb_disease d RIGHT JOIN tb_item_rela_cate r on d.id = r.item_id ) RIGHT JOIN tb_cate c on r.cate_id = c.id

    WHERE (
	   	d.id LIKE #{search}
	   	OR	d.name LIKE #{search} 		 
	   	OR	d.symbol LIKE #{search} 		 
	    OR  part LIKE #{search}
	    OR  depart LIKE #{search}
	    OR  complication LIKE #{search}
	    OR  symptoms LIKE #{search}
	    OR  rate LIKE #{search}
	    OR  time LIKE #{search})

      AND c.id = #{cid}

    ORDER BY
    ${orderCol}
    ${orderDir}

