mapper.select:
    SELECT 
		DISTINCT(m.id) id,
		m.logo logo,
		m.goods_name goods_name,
		CONCAT(m.spec,"/",m.unit) spec,
		approval_number,
		manufacturer,
		bar_code,
		indications,
		otc,
		price,
		m.created,
		m.updated,
		m.`status`
	FROM 

    	tb_medicine m 


    	(tb_medicine m RIGHT JOIN tb_item_rela_cate r on m.id = r.item_id ) RIGHT JOIN tb_cate c on r.cate_id = c.id

	WHERE (
		m.id LIKE #{search}
		OR	m.goods_name LIKE #{search} 		 
		OR	m.spec LIKE #{search} 		 
		OR  approval_number LIKE #{search}
		OR  manufacturer LIKE #{search}
		OR  bar_code LIKE #{search}
		OR  indications LIKE #{search}
		OR  instruction LIKE #{search}
		OR  price LIKE #{search})

      AND c.id = #{cid}

    ORDER BY
    ${orderCol}
    ${orderDir}

